<?php// : Class 'XSLTProcessor' not found extension=php_xsl.dllfunction trans($fx,$fs){   if (!is_file($fs)){echo "<BR>Нет файла $fs для $fx"; return;}   if (!is_file($fx)){echo "<BR>Нет файла $fx"; return;}   $xslDoc = new DOMDocument();   $xslDoc->load($fs);      $xmlDoc = new DOMDocument();   $xmlDoc->load($fx);   $proc = new XSLTProcessor();   $proc->importStylesheet($xslDoc);   return $proc->transformToXML($xmlDoc);}function trans_pars($fx,$fs,$month,$w0,$wc,$md,$year){//PPS/fam-r.xml rasp_month.xsl -o PPS/fam-m9.xml month=9 w0=1 wc=0 md=30//PPS/fam-r.xml  rasp_month.xsl -o PPS/fam-m10.xml month=10 w0=3 wc=0 md=31//PPS/fam-r.xml  rasp_month.xsl -o PPS/fam-m11.xml month=11 w0=6 wc=0 md=30//PPS/fam-r.xml  rasp_month.xsl -o PPS/fam-m12.xml month=12 w0=1 wc=1 md=24//PPS/fam-r.xml  rasp_montxt.xsl -o PPS/fam-m9.txt month=9 w0=1 wc=0 md=30//..   if (!is_file($fs)){echo "<BR>Нет файла $fs для $fx"; return;}   if (!is_file($fx)){echo "<BR>Нет файла $fx"; return;}   $xslDoc = new DOMDocument();   $xslDoc->load($fs);  // xsl   $xmlDoc = new DOMDocument();   $xmlDoc->load($fx);  // xml   $proc = new XSLTProcessor();   $proc->importStylesheet($xslDoc);   $proc->setParameter('', 'month', $month);   $proc->setParameter('', 'w0', $w0);   $proc->setParameter('', 'wc', $wc);   $proc->setParameter('', 'md', $md);   $proc->setParameter('', 'year', $year);   return $proc->transformToXML($xmlDoc);}function trans_pars2($fx,$fs,$year,$mod){   if (!is_file($fs)){echo "<BR>lib_xsl:Нет файла $fs для $fx"; return;}   if (!is_file($fx)){echo "<BR>lib_xsl:Нет файла $fx"; return;}   $xslDoc = new DOMDocument();   $xslDoc->load($fs);  // xsl   $xmlDoc = new DOMDocument();   $xmlDoc->load($fx);  // xml   $proc = new XSLTProcessor();   $proc->importStylesheet($xslDoc);   $proc->setParameter('', 'year', $year);   $proc->setParameter('', 'mod', $mod);   return $proc->transformToXML($xmlDoc);}function trans_pars1($fx,$fs,$sem){   if (!is_file($fs)){echo "<BR>lib_xsl:Нет файла $fs для $fx"; return;}   if (!is_file($fx)){echo "<BR>lib_xsl:Нет файла $fx"; return;}   $xslDoc = new DOMDocument();   $xslDoc->load($fs);  // xsl   $xmlDoc = new DOMDocument();   $xmlDoc->load($fx);  // xml   $proc = new XSLTProcessor();   $proc->importStylesheet($xslDoc);   $proc->setParameter('', 'sem', $sem);   return $proc->transformToXML($xmlDoc);}/*$collections = array(    'Marc Rutkowski' => 'marc',    'Olivier Parmentier' => 'olivier');$xsl = new DOMDocument;$xsl->load('collection.xsl');// Настройка преобразования$proc = new XSLTProcessor;$proc->importStyleSheet($xsl); // добавление стилей xslforeach ($collections as $name => $file) {    // Load the XML source    $xml = new DOMDocument;    $xml->load('collection_' . $file . '.xml');    $proc->setParameter('', 'owner', $name);    $proc->transformToURI($xml, 'file:///tmp/' . $file . '.html');}*/?>